const Hero = () => {
    return (
      <div className="h-screen relative">
        <div className="absolute inset-0 bg-[url('/src/assets/hero-bg.jpg')] bg-cover bg-center">

          <div className="absolute inset-0 bg-black/50" />
        </div>
        <div className="relative h-full flex flex-col justify-center items-center text-center px-4">
          <h1 className="text-5xl md:text-7xl font-playfair mb-4">
            Welcome to Luxe Lounge
          </h1>
          <p className="text-xl md:text-2xl mb-8">
            Experience Luxury & Sophistication
          </p>
          <button className="bg-gold text-black px-8 py-3 rounded hover:bg-gold/80 transition-colors">
            Reserve Now
          </button>
        </div>
      </div>
    );
  };

  export default Hero;
