const Menu = () => {
    const menuItems = [
      {
        category: "Signature Cocktails",
        items: [
          { name: "Golden Sunset", price: "18", description: "Premium vodka, passion fruit, gold leaf" },
          { name: "Midnight Blue", price: "16", description: "Gin, blue curaçao, elderflower" },
        ]
      },
      {
        category: "Classic Cocktails",
        items: [
          { name: "Old Fashioned", price: "14", description: "Bourbon, bitters, sugar" },
          { name: "Negroni", price: "15", description: "Gin, Campari, vermouth" },
        ]
      }
    ];
    return (
      <div className="py-20 bg-black">
        <div className="max-w-7xl mx-auto px-4">
          <h2 className="text-4xl font-playfair text-center mb-16">Our Menu</h2>
          <div className="grid md:grid-cols-2 gap-12">
            {menuItems.map((section) => (
              <div key={section.category}>
                <h3 className="text-2xl text-gold mb-6">{section.category}</h3>
                <div className="space-y-6">
                  {section.items.map((item) => (
                    <div key={item.name} className="border-b border-gray-800 pb-4">
                      <div className="flex justify-between mb-2">
                        <h4 className="text-xl">{item.name}</h4>
                        <span className="text-gold">${item.price}</span>
                      </div>
                      <p className="text-gray-400">{item.description}</p>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  export default Menu;
