import { motion } from "framer-motion";
import { useInView } from "framer-motion";
import { useRef } from "react";

const Gallery = () => {
  const container = {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3
      }
    }
  };

  const item = {
    hidden: { 
      opacity: 0, 
      y: 50 
    },
    show: { 
      opacity: 1, 
      y: 0,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  return (
    <section id="gallery" className="py-16 bg-gray-100">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center mb-12">Notre Galerie</h2>
        <motion.div 
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
          variants={container}
          initial="hidden"
          whileInView="show"
          viewport={{ once: true }}
        >
          {/* Image 1 */}
          <motion.div 
            className="relative overflow-hidden rounded-lg"
            variants={item}
            whileHover={{ scale: 1.05 }}
          >
            <Image
              src="/images/gallery1.jpg"
              alt="Gallery image 1"
              width={400}
              height={300}
              className="object-cover w-full h-full"
            />
          </motion.div>

          {/* Image 2 */}
          <motion.div 
            className="relative overflow-hidden rounded-lg"
            variants={item}
            whileHover={{ scale: 1.05 }}
          >
            <Image
              src="/images/gallery2.jpg"
              alt="Gallery image 2"
              width={400}
              height={300}
              className="object-cover w-full h-full"
            />
          </motion.div>

          {/* Image 3 */}
          <motion.div 
            className="relative overflow-hidden rounded-lg"
            variants={item}
            whileHover={{ scale: 1.05 }}
          >
            <Image
              src="/images/gallery3.jpg"
              alt="Gallery image 3"
              width={400}
              height={300}
              className="object-cover w-full h-full"
            />
          </motion.div>

          {/* Ajoute d'autres images de la même manière */}
        </motion.div>
      </div>
    </section>
  );
};

export default Gallery;
