import { motion } from "framer-motion";
import { useInView } from "framer-motion";
import { useRef } from "react";
import Image from 'next/image';

const Gallery = () => {
  const container = {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3
      }
    }
  };

  const item = {
    hidden: { 
      opacity: 0, 
      y: 50 
    },
    show: { 
      opacity: 1, 
      y: 0,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  const galleryImages = [
    {
      src: "/images/gallery1.jpg",
      alt: "Equipement de fitness 1"
    },
    {
      src: "/images/gallery2.jpg",
      alt: "Equipement de fitness 2"
    },
    {
      src: "/images/gallery3.jpg",
      alt: "Equipement de fitness 3"
    },
    {
      src: "/images/gallery4.jpg",
      alt: "Equipement de fitness 4"
    },
    {
      src: "/images/gallery5.jpg",
      alt: "Equipement de fitness 5"
    },
    {
      src: "/images/gallery6.jpg",
      alt: "Equipement de fitness 6"
    }
  ];

  return (
    <section id="gallery" className="gallery-container">
      <div className="container mx-auto px-4">
        <h2 className="gallery-title">Notre Galerie</h2>
        <motion.div 
          className="gallery-grid"
          variants={container}
          initial="hidden"
          whileInView="show"
          viewport={{ once: true }}
        >
          {galleryImages.map((image, index) => (
            <motion.div 
              key={index}
              className="gallery-item"
              variants={item}
              whileHover={{ scale: 1.05 }}
            >
              <Image
                src={image.src}
                alt={image.alt}
                width={400}
                height={300}
                className="gallery-image"
              />
              <div className="gallery-overlay">
                <p>{image.alt}</p>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Gallery;
