import { motion, AnimatePresence } from 'framer-motion';
import { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { FaBars, FaTimes } from 'react-icons/fa';
import Logo from '../assets/logo.png';
import './Navbar.css';

const Navbar = () => {
  const [nav, setNav] = useState(false);

  const menuItems = [
    { title: 'Home', path: '/' },
    { title: 'About', path: '/about' },
    { title: 'Menu', path: '/menu' },
    { title: 'Gallery', path: '/gallery' },
    { title: 'Contact', path: '/contact' },
  ];

  return (
    <motion.nav
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
      className="fixed w-full h-20 bg-black/90 backdrop-blur-sm z-50"
    >
      {/* Ajout de pl-4 pour un padding à gauche */}
      <div className="max-w-7xl mx-auto h-full flex justify-between items-center pl-4"> 
        {/* Le logo */}
        <NavLink to="/" className="flex-shrink-0"> {/* flex-shrink-0 empêche le logo de se rétrécir */}
          <motion.img
            whileHover={{ scale: 1.05 }}
            src={Logo}
            alt="Logo"
            className="h-12 md:h-16"
          />
        </NavLink>

        {/* Menu pour desktop */}
        <div className="hidden md:flex flex-grow justify-center"> {/* justify-center centre les éléments */}
          <ul className="flex space-x-8">
            {menuItems.map(({ title, path }, index) => (
              <motion.li
                key={title}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
              >
                <NavLink
                  to={path}
                  className={({ isActive }) =>
                    isActive 
                      ? "text-gold border-b-2 border-gold pb-1 transition-all duration-300"
                      : "text-white hover:text-gold hover:border-b-2 hover:border-gold pb-1 transition-all duration-300"
                  }
                >
                  {title}
                </NavLink>
              </motion.li>
            ))}
          </ul>
        </div>

        {/* Bouton menu mobile */}
        <motion.div
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          className="md:hidden cursor-pointer text-white hover:text-gold transition-colors duration-300 pr-4"
          onClick={() => setNav(!nav)}
        >
          {nav ? <FaTimes size={25} /> : <FaBars size={25} />}
        </motion.div>
      </div>

      {/* Menu mobile */}
      <AnimatePresence>
        {nav && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
            className="md:hidden absolute top-20 left-0 w-full bg-black/95"
          >
            <ul className="px-4 py-6 space-y-4">
              {menuItems.map(({ title, path }, index) => (
                <motion.li
                  key={title}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: index * 0.1 }}
                >
                  <NavLink
                    to={path}
                    className={({ isActive }) =>
                      isActive 
                        ? "text-gold border-b-2 border-gold pb-1 transition-all duration-300 block"
                        : "text-white hover:text-gold hover:border-b-2 hover:border-gold pb-1 transition-all duration-300 block"
                    }
                    onClick={() => setNav(false)}
                  >
                    {title}
                  </NavLink>
                </motion.li>
              ))}
            </ul>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.nav>
  );
};

export default Navbar;
