import { useState } from 'react';
import { FaBars, FaTimes } from 'react-icons/fa';
import Logo from "../assets/logo.png";

const Navbar = () => {
  const [nav, setNav] = useState(false);

  return (
    <nav className="fixed w-full h-20 bg-black/90 backdrop-blur-sm z-50">
      <div className="max-w-7xl mx-auto px-4 h-full flex justify-between items-center">
        {/* Logo */}
        <img 
          src={Logo} 
          alt="Luxe Lounge Logo" 
          className="h-12 md:h-16 cursor-pointer"
        />

        <div className="hidden md:flex">
          <ul className="flex space-x-8">
            {['Home', 'About', 'Menu', 'Gallery', 'Contact'].map((item) => (
              <li key={item} className="hover:text-gold cursor-pointer transition-colors">
                {item}
              </li>
            ))}
          </ul>
        </div>

        {/* Mobile menu button */}
        <div className="md:hidden cursor-pointer" onClick={() => setNav(!nav)}>
          {nav ? <FaTimes size={25} /> : <FaBars size={25} />}
        </div>
      </div>

      {/* Mobile menu */}
      {nav && (
        <div className="md:hidden absolute top-20 left-0 w-full bg-black/95">
          <ul className="px-4 py-6 space-y-4">
            {['Home', 'About', 'Menu', 'Gallery', 'Contact'].map((item) => (
              <li key={item} className="hover:text-gold cursor-pointer transition-colors">
                {item}
              </li>
            ))}
          </ul>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
